import axios from 'axios';

const API_BASE_URL = 'https://hopewellapi-azcvcferesfpgjgm.southafricanorth-01.azurewebsites.net/api';

// Create axios instance with default config
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle common errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Only logout if this is an authentication-related endpoint or if we're in mock mode
      const isAuthEndpoint = error.config?.url?.includes('/Auth/');
      const isLoginEndpoint = error.config?.url?.includes('/login');
      const isRegisterEndpoint = error.config?.url?.includes('/register');

      // In mock mode, don't auto-logout on 401 errors unless it's an auth endpoint
      if (MOCK_MODE) {
        if (isAuthEndpoint && (isLoginEndpoint || isRegisterEndpoint)) {
          // Only logout on actual login/register failures
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          if (window.location.pathname !== '/auth') {
            window.location.href = '/auth';
          }
        }
        // For all other 401s in mock mode, just log and continue
        console.log('⚠️ Mock Mode: Ignoring 401 error for:', error.config?.url);
      } else {
        // Real API mode - be more careful about when to logout
        if (isAuthEndpoint) {
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          if (window.location.pathname !== '/auth') {
            window.location.href = '/auth';
          }
        }
      }
    }
    return Promise.reject(error);
  }
);

// Real API only - no mock mode

// Auth API
export const authAPI = {
  register: async (userData) => {

    try {
      console.log('Attempting registration to:', API_BASE_URL + '/Auth/register');
      console.log('Registration data:', userData);
      const response = await api.post('/Auth/register', userData);
      console.log('Registration response:', response.data);
      return response.data;
    } catch (error) {
      console.error('Registration error:', error);
      console.error('Error response:', error.response?.data);
      console.error('Error status:', error.response?.status);
      throw error.response?.data || error.message;
    }
  },

  login: async (credentials) => {
    if (MOCK_MODE) {
      console.log('🔐 Mock Login Attempt:', credentials);

      // Mock successful login with role detection
      return new Promise((resolve) => {
        setTimeout(() => {
          let userRole = "patient";
          let firstName = "Mock";
          let lastName = "User";

          // Detect role based on email
          if (credentials.email.includes('doctor')) {
            userRole = "doctor";
            firstName = "Dr. John";
            lastName = "Smith";
          } else if (credentials.email.includes('nurse')) {
            userRole = "nurse";
            firstName = "Jane";
            lastName = "Nurse";
          } else if (credentials.email.includes('admin')) {
            userRole = "admin";
            firstName = "Admin";
            lastName = "User";
          } else if (credentials.email.includes('patient')) {
            userRole = "patient";
            firstName = "John";
            lastName = "Patient";
          }

          const mockResponse = {
            token: "mock-jwt-token-" + Date.now(),
            user: {
              id: "mock-user-id-" + userRole,
              email: credentials.email,
              firstName: firstName,
              lastName: lastName,
              roles: [userRole]
            }
          };

          console.log('✅ Mock Login Success:', mockResponse);
          resolve(mockResponse);
        }, 1000);
      });
    }

    try {
      console.log('Attempting login to:', API_BASE_URL + '/Auth/login');
      console.log('Credentials:', credentials);
      const response = await api.post('/Auth/login', credentials);
      console.log('Login response:', response.data);
      return response.data;
    } catch (error) {
      console.error('Login error:', error);
      console.error('Error response:', error.response?.data);
      console.error('Error status:', error.response?.status);
      throw error.response?.data || error.message;
    }
  },

  logout: async () => {
    if (MOCK_MODE) {
      // Mock successful logout
      return new Promise((resolve) => {
        setTimeout(() => {
          resolve({ message: "Logged out successfully" });
        }, 500);
      });
    }

    try {
      const response = await api.post('/Auth/logout');
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },
};

