import axios from 'axios';

const API_BASE_URL = 'http://localhost:5002/api';

// Create axios instance with default config
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle common errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Token expired or invalid
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/auth';
    }
    return Promise.reject(error);
  }
);

// Auth API
export const authAPI = {
  register: async (userData) => {
    try {
      const response = await api.post('/Auth/register', userData);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  login: async (credentials) => {
    try {
      const response = await api.post('/Auth/login', credentials);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  logout: async () => {
    try {
      const response = await api.post('/Auth/logout');
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },
};

// Services API
export const servicesAPI = {
  getAll: async () => {
    try {
      const response = await api.get('/Services');
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  create: async (serviceData) => {
    try {
      const response = await api.post('/Services', serviceData);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  update: async (id, serviceData) => {
    try {
      const response = await api.put(`/Services/${id}`, serviceData);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  delete: async (id) => {
    try {
      const response = await api.delete(`/Services/${id}`);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },
};

// Time Slots API
export const timeSlotsAPI = {
  getByDay: async (day) => {
    try {
      const response = await api.get(`/time-slots/by-day/${day}`);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  getAvailable: async (date) => {
    try {
      const params = date ? { date } : {};
      const response = await api.get('/time-slots/available', { params });
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },
};

// Staff API
export const staffAPI = {
  getAll: async () => {
    try {
      const response = await api.get('/Staff');
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  getById: async (id) => {
    try {
      const response = await api.get(`/Staff/${id}`);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  getByRole: async (role) => {
    try {
      const response = await api.get(`/Staff/by-role/${role}`);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  getSchedule: async (id, startDate, endDate) => {
    try {
      const params = {};
      if (startDate) params.startDate = startDate;
      if (endDate) params.endDate = endDate;
      const response = await api.get(`/Staff/${id}/schedule`, { params });
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  getAvailability: async (id, date) => {
    try {
      const response = await api.get(`/Staff/${id}/availability`, {
        params: { date }
      });
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  update: async (id, staffData) => {
    try {
      const response = await api.put(`/Staff/${id}`, staffData);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  updateAvailability: async (id, availabilityData) => {
    try {
      const response = await api.post(`/Staff/${id}/availability`, availabilityData);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },
};

// Appointments API
export const appointmentsAPI = {
  getAll: async () => {
    try {
      const response = await api.get('/Appointments');
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  getById: async (id) => {
    try {
      const response = await api.get(`/Appointments/${id}`);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  getByPatient: async (patientId) => {
    try {
      const response = await api.get(`/Appointments/patient/${patientId}`);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  getToday: async () => {
    try {
      const response = await api.get('/Appointments/today');
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  create: async (appointmentData) => {
    try {
      const response = await api.post('/Appointments', appointmentData);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  update: async (id, appointmentData) => {
    try {
      const response = await api.put(`/Appointments/${id}`, appointmentData);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  updateStatus: async (id, status) => {
    try {
      const response = await api.put(`/Appointments/${id}/status`, { status });
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  delete: async (id) => {
    try {
      const response = await api.delete(`/Appointments/${id}`);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  assignStaff: async (id, staffId) => {
    try {
      const response = await api.post(`/Appointments/${id}/assign-staff`, { staffId });
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  bookForPatient: async (appointmentData) => {
    try {
      const response = await api.post('/Appointments/book-for-patient', appointmentData);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },
};

// Patients API
export const patientsAPI = {
  getAll: async () => {
    try {
      const response = await api.get('/Patients');
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  getById: async (id) => {
    try {
      const response = await api.get(`/Patients/${id}`);
      return response.data;
    } catch (error) {
