import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { Badge } from './ui/badge';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { appointmentsAPI, doctorAPI, staffAPI, adminAPI } from '../services/api';
import BookingWizard from './BookingWizard';
import { 
  Calendar, 
  Clock, 
  Users, 
  CheckCircle, 
  XCircle, 
  AlertCircle, 
  User, 
  FileText, 
  Settings,
  LogOut,
  Plus,
  Eye,
  Check,
  X,
  BarChart3,
  UserPlus,
  Shield,
  Activity,
  UserCog,
  CalendarDays
} from 'lucide-react';

interface User {
  id: string;
  email: string;
  firstName: string;
  lastName: string;
  role: string;
}

interface AdminUser {
  id: string;
  email: string;
  firstName: string;
  lastName: string;
  role: string;
  isActive: boolean;
  createdAt: string;
}

interface Appointment {
  id: string;
  appointmentDate: string;
  startTime: string;
  endTime: string;
  status: string;
  notes?: string;
  patient: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
  };
  service: {
    id: string;
    name: string;
    durationMinutes: number;
    price?: number;
  };
  staff?: {
    id: string;
    firstName: string;
    lastName: string;
  };
}

interface Service {
  id: string;
  name: string;
  description?: string;
  durationMinutes: number;
  price?: number;
}

interface Doctor {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  role: string;
  isActive: boolean;
  createdAt?: string;
}

interface ShiftSchedule {
  dayOfWeek: string;
  startTime: string;
  endTime: string;
  isActive: boolean;
}

interface AdminDashboardProps {
  user: User;
  users: AdminUser[];
  appointments: Appointment[];
  services: Service[];
  isLoading: boolean;
  isProcessing: boolean;
  error: string;
  onToggleUserStatus: (userId: string, isActive: boolean) => Promise<void>;
  onUpdateUserRole: (userId: string, newRole: string) => Promise<void>;
  onCreateStaff: (staffData: any) => Promise<void>;
  onCreateService: (serviceData: any) => Promise<void>;
  onUpdateService: (serviceId: string, serviceData: any) => Promise<void>;
  onDeleteService: (serviceId: string) => Promise<void>;
  onLogout: () => void;
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({
  user,
  users,
  appointments,
  services,
  isLoading,
  isProcessing,
  error,
  onToggleUserStatus,
  onUpdateUserRole,
  onCreateStaff,
  onCreateService,
  onUpdateService,
  onDeleteService,
  onLogout
}) => {
  const [activeTab, setActiveTab] = useState('overview');
  const [showCreateStaffModal, setShowCreateStaffModal] = useState(false);
  const [showCreatePatientModal, setShowCreatePatientModal] = useState(false);
  const [showWalkInModal, setShowWalkInModal] = useState(false);
  const [showBookingWizard, setShowBookingWizard] = useState(false);
  const [selectedPatientId, setSelectedPatientId] = useState<string | null>(null);
  const [showServiceModal, setShowServiceModal] = useState(false);
  const [editingService, setEditingService] = useState<any>(null);

  // Doctor schedule management states
  const [showDoctorScheduleModal, setShowDoctorScheduleModal] = useState(false);
  const [selectedDoctor, setSelectedDoctor] = useState<Doctor | null>(null);
  const [allDoctors, setAllDoctors] = useState<Doctor[]>([]);
  const [doctorShiftSchedule, setDoctorShiftSchedule] = useState<ShiftSchedule[]>([]);
  const [newShiftSchedule, setNewShiftSchedule] = useState<ShiftSchedule[]>([]);
  const [isLoadingDoctors, setIsLoadingDoctors] = useState(false);
  const [isUpdatingSchedule, setIsUpdatingSchedule] = useState(false);
  const [newStaffData, setNewStaffData] = useState({
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    role: 'doctor',
    phone: ''
  });
  const [newPatientData, setNewPatientData] = useState({
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    phone: '',
    dateOfBirth: '',
    address: '',
    emergencyContact: '',
    emergencyPhone: ''
  });
  const [serviceData, setServiceData] = useState({
    name: '',
    description: '',
    durationMinutes: 30,
    price: 0
  });

  // Days of the week for shift schedule
  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

  // Load all doctors for admin to manage
  const loadDoctors = async () => {
    try {
      setIsLoadingDoctors(true);
      const response = await staffAPI.getAll();
      const doctorUsers = response.filter((user: any) => user.role === 'doctor');
      setAllDoctors(doctorUsers);
      console.log('ðŸ“‹ Loaded doctors for admin:', doctorUsers);
    } catch (error) {
      console.error('âŒ Error loading doctors:', error);
      // Fallback: try to get doctors from users list
      const doctorUsers = users.filter(user => user.role === 'doctor');
      setAllDoctors(doctorUsers);
    } finally {
      setIsLoadingDoctors(false);
    }
  };

  // Load doctor's current shift schedule
  const loadDoctorShiftSchedule = async (doctorId: string) => {
    try {
      console.log('ðŸ” Loading shift schedule for doctor:', doctorId);

      // Try admin-specific endpoint first
      try {
        const schedule = await adminAPI.getDoctorShiftSchedule(doctorId);
        setDoctorShiftSchedule(schedule);
        setNewShiftSchedule(schedule);
        console.log('âœ… Loaded shift schedule via admin endpoint:', schedule);
        return;
      } catch (adminError) {
        console.log('âš ï¸ Admin endpoint failed, trying doctor endpoint...');

        // Try doctor endpoint as fallback
        try {
          const schedule = await doctorAPI.getShiftSchedule(doctorId);
          setDoctorShiftSchedule(schedule);
          setNewShiftSchedule(schedule);
          console.log('âœ… Loaded shift schedule via doctor endpoint:', schedule);
          return;
        } catch (doctorError) {
          console.log('âš ï¸ Doctor endpoint failed, trying public endpoint...');

          // Try public endpoint as final fallback
          try {
            const publicSchedule = await doctorAPI.getShiftSchedulePublic(doctorId);
            setDoctorShiftSchedule(publicSchedule);
            setNewShiftSchedule(publicSchedule);
            console.log('âœ… Loaded schedule from public endpoint:', publicSchedule);
            return;
          } catch (publicError) {
            console.error('âŒ All endpoints failed:', publicError);
            throw publicError;
          }
        }
      }
    } catch (error) {
      console.error('âŒ Error loading shift schedule:', error);

      // Initialize with default schedule if all fail
      const defaultSchedule = daysOfWeek.map(day => ({
        dayOfWeek: day,
        startTime: '09:00',
        endTime: '17:00',
        isActive: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'].includes(day)
      }));
      setDoctorShiftSchedule(defaultSchedule);
      setNewShiftSchedule(defaultSchedule);
    }
  };

  // Update doctor's shift schedule
  const updateDoctorShiftSchedule = async () => {
    if (!selectedDoctor) return;

    try {
      setIsUpdatingSchedule(true);
      console.log('ðŸ”„ Updating shift schedule for doctor:', selectedDoctor.id);
      console.log('ðŸ“… New schedule:', newShiftSchedule);

      // Use the working Doctor endpoint that admins can access
      await doctorAPI.updateShiftSchedule(selectedDoctor.id, newShiftSchedule);
      setDoctorShiftSchedule(newShiftSchedule);
      console.log('âœ… Shift schedule updated successfully');

      // Close modal after successful update
      setShowDoctorScheduleModal(false);
      setSelectedDoctor(null);
    } catch (error) {
      console.error('âŒ Error updating shift schedule:', error);

      // Check if it's an authentication error
      if (error && typeof error === 'object' && 'response' in error && 
          error.response && typeof error.response === 'object' && 
          'status' in error.response && error.response.status === 401) {
        console.log('âš ï¸ Authentication failed - admin may not have proper permissions');
        console.log('ðŸ’¡ Schedule changes will be saved locally but not persisted to database');

        // Still update local state for immediate feedback
        setDoctorShiftSchedule(newShiftSchedule);

        // Show user-friendly message
        alert('Schedule updated locally, but could not save to database. Please check admin permissions.');

        // Close modal after local update
        setShowDoctorScheduleModal(false);
        setSelectedDoctor(null);
      } else {
        // For other errors, still update local state
        setDoctorShiftSchedule(newShiftSchedule);

        // Close modal after local update
        setShowDoctorScheduleModal(false);
        setSelectedDoctor(null);
      }
    } finally {
      setIsUpdatingSchedule(false);
    }
  };

  // Handle doctor selection for schedule management
  const handleDoctorSelection = async (doctor: Doctor) => {
    setSelectedDoctor(doctor);
    setShowDoctorScheduleModal(true);
    await loadDoctorShiftSchedule(doctor.id);
  };

