                      <span className="font-medium">{appointments.length}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-sm text-muted-foreground">Confirmed</span>
                      <span className="font-medium text-green-600">
                        {appointments.filter(apt => apt.status === 'confirmed').length}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-sm text-muted-foreground">Pending</span>
                      <span className="font-medium text-yellow-600">
                        {appointments.filter(apt => apt.status === 'pending').length}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-sm text-muted-foreground">Cancelled</span>
                      <span className="font-medium text-red-600">
                        {appointments.filter(apt => apt.status === 'cancelled').length}
                      </span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Service Usage */}
              <Card className="medical-card">
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <Settings className="h-5 w-5 text-primary" />
                    <span>Service Usage</span>
                  </CardTitle>
                  <CardDescription>
                    Most popular services
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    {services.map((service) => {
                      const usageCount = appointments.filter(apt => apt.service.id === service.id).length;
                      return (
                        <div key={service.id} className="flex justify-between">
                          <span className="text-sm text-muted-foreground">{service.name}</span>
                          <span className="font-medium">{usageCount}</span>
                        </div>
                      );
                    })}
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Revenue Report */}
            <Card className="medical-card">
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <BarChart3 className="h-5 w-5 text-primary" />
                  <span>Revenue Report</span>
                </CardTitle>
                <CardDescription>
                  Financial overview and trends
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex justify-between">
                    <span className="text-sm text-muted-foreground">Total Revenue</span>
                    <span className="font-medium text-green-600">
                      ${appointments
                        .filter(apt => apt.status === 'confirmed' || apt.status === 'completed')
                        .reduce((total, apt) => total + (apt.service.price || 0), 0)
                        .toFixed(2)}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-muted-foreground">This Month</span>
                    <span className="font-medium">
                      ${appointments
                        .filter(apt => {
                          const aptDate = new Date(apt.appointmentDate);
                          const now = new Date();
                          return aptDate.getMonth() === now.getMonth() && 
                                 aptDate.getFullYear() === now.getFullYear() &&
                                 (apt.status === 'confirmed' || apt.status === 'completed');
                        })
                        .reduce((total, apt) => total + (apt.service.price || 0), 0)
                        .toFixed(2)}
                    </span>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        )}
      </div>

      {/* Create Staff Modal */}
      {showCreateStaffModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <Card className="w-full max-w-md max-h-[90vh] overflow-y-auto">
            <CardHeader>
              <CardTitle>Add Staff Member</CardTitle>
              <CardDescription>
                Create a new staff member account
              </CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleCreateStaff} className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="firstName">First Name</Label>
                    <Input
                      id="firstName"
                      name="firstName"
                      value={newStaffData.firstName}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div>
                    <Label htmlFor="lastName">Last Name</Label>
                    <Input
                      id="lastName"
                      name="lastName"
                      value={newStaffData.lastName}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    name="email"
                    type="email"
                    value={newStaffData.email}
                    onChange={handleInputChange}
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="password">Password</Label>
                  <Input
                    id="password"
                    name="password"
                    type="password"
                    value={newStaffData.password}
                    onChange={handleInputChange}
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="phone">Phone</Label>
                  <Input
                    id="phone"
                    name="phone"
                    value={newStaffData.phone}
                    onChange={handleInputChange}
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="role">Role</Label>
                  <select
                    id="role"
                    name="role"
                    value={newStaffData.role}
                    onChange={handleInputChange}
                    className="w-full h-10 px-3 py-2 border border-input bg-background rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2"
                    required
                  >
                    <option value="doctor">Doctor</option>
                    <option value="nurse">Nurse</option>
                    <option value="admin">Admin</option>
                  </select>
                </div>

                <div className="flex space-x-3 pt-4">
                  <Button
                    type="button"
                    variant="outline"
                    className="flex-1"
                    onClick={() => setShowCreateStaffModal(false)}
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    className="flex-1"
                    disabled={isProcessing}
                  >
                    {isProcessing ? 'Creating...' : 'Create Staff'}
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Service Management Modal */}
      {showServiceModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <Card className="w-full max-w-md max-h-[90vh] overflow-y-auto">
            <CardHeader>
              <CardTitle>{editingService ? 'Edit Service' : 'Add New Service'}</CardTitle>
              <CardDescription>
                {editingService ? 'Update service information' : 'Create a new clinic service'}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={async (e) => {
                e.preventDefault();
                if (editingService) {
                  await onUpdateService(editingService.id, serviceData);
                } else {
                  await onCreateService(serviceData);
                }
                setShowServiceModal(false);
                setEditingService(null);
                setServiceData({
                  name: '',
                  description: '',
                  durationMinutes: 30,
                  price: 0
                });
              }} className="space-y-4">
                <div>
                  <Label htmlFor="serviceName">Service Name</Label>
                  <Input
                    id="serviceName"
                    value={serviceData.name}
                    onChange={(e) => setServiceData(prev => ({ ...prev, name: e.target.value }))}
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="serviceDescription">Description</Label>
                  <textarea
                    id="serviceDescription"
                    value={serviceData.description}
                    onChange={(e) => setServiceData(prev => ({ ...prev, description: e.target.value }))}
                    className="w-full h-20 px-3 py-2 border border-input bg-background rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 resize-none"
                    placeholder="Service description..."
                  />
                </div>

                <div>
                  <Label htmlFor="durationMinutes">Duration (minutes)</Label>
                  <Input
                    id="durationMinutes"
                    type="number"
                    min="15"
                    max="240"
                    step="15"
                    value={serviceData.durationMinutes}
                    onChange={(e) => setServiceData(prev => ({ ...prev, durationMinutes: parseInt(e.target.value) }))}
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="price">Price ($)</Label>
                  <Input
                    id="price"
                    type="number"
                    min="0"
                    step="0.01"
                    value={serviceData.price}
                    onChange={(e) => setServiceData(prev => ({ ...prev, price: parseFloat(e.target.value) }))}
                  />
                </div>

                <div className="flex space-x-3 pt-4">
                  <Button
                    type="button"
                    variant="outline"
                    className="flex-1"
                    onClick={() => {
                      setShowServiceModal(false);
                      setEditingService(null);
                      setServiceData({
                        name: '',
                        description: '',
                        durationMinutes: 30,
                        price: 0
                      });
                    }}
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    className="flex-1"
                    disabled={isProcessing}
                  >
                    {isProcessing ? 'Saving...' : (editingService ? 'Update Service' : 'Create Service')}
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Create Patient Modal */}
      {showCreatePatientModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <Card className="w-full max-w-md max-h-[90vh] overflow-y-auto">
            <CardHeader>
              <CardTitle>Add New Patient</CardTitle>
              <CardDescription>
                Create a new patient account
              </CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleCreatePatient} className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="firstName">First Name</Label>
                    <Input
                      id="firstName"
                      name="firstName"
                      value={newPatientData.firstName}
                      onChange={handlePatientInputChange}
                      required
                    />
                  </div>
                  <div>
                    <Label htmlFor="lastName">Last Name</Label>
                    <Input
                      id="lastName"
                      name="lastName"
                      value={newPatientData.lastName}
                      onChange={handlePatientInputChange}
                      required
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    name="email"
                    type="email"
                    value={newPatientData.email}
                    onChange={handlePatientInputChange}
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="password">Password</Label>
                  <Input
                    id="password"
                    name="password"
                    type="password"
                    value={newPatientData.password}
                    onChange={handlePatientInputChange}
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="phone">Phone</Label>
                  <Input
                    id="phone"
                    name="phone"
                    value={newPatientData.phone}
                    onChange={handlePatientInputChange}
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="dateOfBirth">Date of Birth</Label>
                  <Input
                    id="dateOfBirth"
                    name="dateOfBirth"
                    type="date"
                    value={newPatientData.dateOfBirth}
                    onChange={handlePatientInputChange}
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="address">Address</Label>
                  <Input
                    id="address"
                    name="address"
                    value={newPatientData.address}
                    onChange={handlePatientInputChange}
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="emergencyContact">Emergency Contact</Label>
                  <Input
                    id="emergencyContact"
                    name="emergencyContact"
                    value={newPatientData.emergencyContact}
                    onChange={handlePatientInputChange}
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="emergencyPhone">Emergency Phone</Label>
                  <Input
                    id="emergencyPhone"
                    name="emergencyPhone"
                    value={newPatientData.emergencyPhone}
                    onChange={handlePatientInputChange}
                    required
                  />
                </div>

                <div className="flex space-x-3 pt-4">
                  <Button
                    type="button"
                    variant="outline"
                    className="flex-1"
                    onClick={() => setShowCreatePatientModal(false)}
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    className="flex-1"
                    disabled={isProcessing}
                  >
                    {isProcessing ? 'Creating...' : 'Create Patient'}
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Patient Selection Modal for Walk-in */}
      {showWalkInModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <Card className="w-full max-w-md max-h-[90vh] overflow-y-auto">
            <CardHeader>
              <CardTitle>Select Patient for Walk-in Appointment</CardTitle>
              <CardDescription>
                Choose the patient to book an appointment for
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {patients.length === 0 ? (
                  <div className="text-center py-8">
                    <User className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                    <p className="text-muted-foreground">No patients found</p>
                    <p className="text-sm text-muted-foreground mt-2">
                      Create a patient first using the "Add Patient" button
                    </p>
                  </div>
                ) : (
                  <div className="space-y-2 max-h-60 overflow-y-auto">
                    {patients.map((patient) => (
                      <div
                        key={patient.id}
                        className="border rounded-lg p-4 hover:bg-muted/50 transition-colors cursor-pointer"
                        onClick={() => handlePatientSelection(patient.id)}
                      >
                        <div className="flex items-center justify-between">
                          <div>
                            <h4 className="font-medium text-foreground">
                              {patient.firstName} {patient.lastName}
                            </h4>
                            <p className="text-sm text-muted-foreground">{patient.email}</p>
                          </div>
                          <Button size="sm" variant="outline">
                            Select
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}

                <div className="flex space-x-3 pt-4">
                  <Button
                    variant="outline"
                    className="flex-1"
                    onClick={() => setShowWalkInModal(false)}
                  >
                    Cancel
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Doctor Schedule Management Modal */}
      {showDoctorScheduleModal && selectedDoctor && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <Card className="w-full max-w-4xl max-h-[90vh] overflow-y-auto">
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <CalendarDays className="h-5 w-5 text-primary" />
                <span>Manage Schedule - Dr. {selectedDoctor.firstName} {selectedDoctor.lastName}</span>
              </CardTitle>
              <CardDescription>
                Set the weekly shift schedule for this doctor
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-6">
                {/* Quick Action Buttons */}
                <div className="flex flex-wrap gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      const weekdaysSchedule = daysOfWeek.map(day => ({
                        dayOfWeek: day,
                        startTime: '09:00',
                        endTime: '17:00',
                        isActive: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'].includes(day)
                      }));
                      setNewShiftSchedule(weekdaysSchedule);
                    }}
                  >
                    Set Weekdays (9-5)
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      const allDaysSchedule = daysOfWeek.map(day => ({
                        dayOfWeek: day,
                        startTime: '09:00',
                        endTime: '17:00',
                        isActive: true
                      }));
                      setNewShiftSchedule(allDaysSchedule);
                    }}
                  >
                    Set All Days (9-5)
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      const clearedSchedule = daysOfWeek.map(day => ({
                        dayOfWeek: day,
                        startTime: '09:00',
                        endTime: '17:00',
                        isActive: false
                      }));
                      setNewShiftSchedule(clearedSchedule);
                    }}
                  >
                    Clear All
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      const defaultSchedule = daysOfWeek.map(day => ({
                        dayOfWeek: day,
                        startTime: '09:00',
                        endTime: '17:00',
                        isActive: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'].includes(day)
                      }));
                      setNewShiftSchedule(defaultSchedule);
                    }}
                  >
                    Reset to Defaults
                  </Button>
                </div>

                {/* Schedule Grid */}
                <div className="space-y-4">
                  {newShiftSchedule.map((shift, index) => (
                    <div key={shift.dayOfWeek} className="flex items-center space-x-4 p-4 border rounded-lg">
                      <div className="w-24">
                        <Label className="font-medium">{shift.dayOfWeek}</Label>
                      </div>

                      <div className="flex items-center space-x-2">
                        <Input
                          type="time"
                          value={shift.startTime}
                          onChange={(e) => {
                            const updatedSchedule = [...newShiftSchedule];
                            updatedSchedule[index].startTime = e.target.value;
                            setNewShiftSchedule(updatedSchedule);
                          }}
                          className="w-32"
                        />
                        <span className="text-muted-foreground">to</span>
                        <Input
                          type="time"
                          value={shift.endTime}
                          onChange={(e) => {
                            const updatedSchedule = [...newShiftSchedule];
                            updatedSchedule[index].endTime = e.target.value;
                            setNewShiftSchedule(updatedSchedule);
                          }}
                          className="w-32"
                        />
                      </div>

                      <div className="flex items-center space-x-2">
                        <input
                          type="checkbox"
                          id={`active-${shift.dayOfWeek}`}
                          checked={shift.isActive}
                          onChange={(e) => {
                            const updatedSchedule = [...newShiftSchedule];
                            updatedSchedule[index].isActive = e.target.checked;
                            setNewShiftSchedule(updatedSchedule);
                          }}
                          className="h-4 w-4 text-primary focus:ring-primary border-gray-300 rounded"
                        />
                        <Label htmlFor={`active-${shift.dayOfWeek}`} className="text-sm">
                          Available
                        </Label>
                      </div>
                    </div>
                  ))}
                </div>

                {/* Action Buttons */}
                <div className="flex space-x-3 pt-4 border-t">
                  <Button
                    variant="outline"
                    className="flex-1"
                    onClick={() => {
                      setShowDoctorScheduleModal(false);
                      setSelectedDoctor(null);
                    }}
                  >
                    Cancel
                  </Button>
                  <Button
                    onClick={updateDoctorShiftSchedule}
                    className="flex-1"
                    disabled={isUpdatingSchedule}
                  >
                    {isUpdatingSchedule ? 'Updating...' : 'Update Schedule'}
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Booking Wizard for Walk-in Appointments */}
      {showBookingWizard && selectedPatientId && (
        <BookingWizard
          isOpen={showBookingWizard}
          onClose={() => {
            setShowBookingWizard(false);
            setSelectedPatientId(null);
          }}
          onSuccess={handleBookingSuccess}
          services={services}
          patientId={selectedPatientId}
        />
      )}
    </div>
  );
};

export default AdminDashboard;
