import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { Badge } from './ui/badge';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { appointmentsAPI } from '../services/api';
import BookingWizard from './BookingWizard';
import { 
  Calendar, 
  Clock, 
  Users, 
  CheckCircle, 
  XCircle, 
  AlertCircle, 
  User, 
  FileText, 
  Settings,
  LogOut,
  Plus,
  Eye,
  Check,
  X,
  BarChart3,
  UserPlus,
  Shield,
  Activity
} from 'lucide-react';

interface User {
  id: string;
  email: string;
  firstName: string;
  lastName: string;
  role: string;
}

interface AdminUser {
  id: string;
  email: string;
  firstName: string;
  lastName: string;
  role: string;
  isActive: boolean;
  createdAt: string;
}

interface Appointment {
  id: string;
  appointmentDate: string;
  startTime: string;
  endTime: string;
  status: string;
  notes?: string;
  patient: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
  };
  service: {
    id: string;
    name: string;
    durationMinutes: number;
    price?: number;
  };
  staff?: {
    id: string;
    firstName: string;
    lastName: string;
  };
}

interface Service {
  id: string;
  name: string;
  description?: string;
  durationMinutes: number;
  price?: number;
}

interface AdminDashboardProps {
  user: User;
  users: AdminUser[];
  appointments: Appointment[];
  services: Service[];
  isLoading: boolean;
  isProcessing: boolean;
  error: string;
  onToggleUserStatus: (userId: string, isActive: boolean) => Promise<void>;
  onUpdateUserRole: (userId: string, newRole: string) => Promise<void>;
  onCreateStaff: (staffData: any) => Promise<void>;
  onCreateService: (serviceData: any) => Promise<void>;
  onUpdateService: (serviceId: string, serviceData: any) => Promise<void>;
  onDeleteService: (serviceId: string) => Promise<void>;
  onLogout: () => void;
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({
  user,
  users,
  appointments,
  services,
  isLoading,
  isProcessing,
  error,
  onToggleUserStatus,
  onUpdateUserRole,
  onCreateStaff,
  onCreateService,
  onUpdateService,
  onDeleteService,
  onLogout
}) => {
  const [activeTab, setActiveTab] = useState('overview');
  const [showCreateStaffModal, setShowCreateStaffModal] = useState(false);
  const [showCreatePatientModal, setShowCreatePatientModal] = useState(false);
  const [showWalkInModal, setShowWalkInModal] = useState(false);
  const [showBookingWizard, setShowBookingWizard] = useState(false);
  const [selectedPatientId, setSelectedPatientId] = useState<string | null>(null);
  const [showServiceModal, setShowServiceModal] = useState(false);
  const [editingService, setEditingService] = useState<any>(null);
  const [newStaffData, setNewStaffData] = useState({
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    role: 'doctor',
    phone: ''
  });
  const [newPatientData, setNewPatientData] = useState({
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    phone: '',
    dateOfBirth: '',
    address: '',
    emergencyContact: '',
    emergencyPhone: ''
  });
  const [serviceData, setServiceData] = useState({
    name: '',
    description: '',
    durationMinutes: 30,
    price: 0
  });

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatTime = (timeString: string) => {
    return new Date(`2000-01-01T${timeString}`).toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      confirmed: { variant: 'default' as const, icon: CheckCircle, className: 'bg-green-100 text-green-800' },
      pending: { variant: 'secondary' as const, icon: AlertCircle, className: 'bg-yellow-100 text-yellow-800' },
      cancelled: { variant: 'destructive' as const, icon: XCircle, className: 'bg-red-100 text-red-800' },
      completed: { variant: 'outline' as const, icon: CheckCircle, className: 'bg-blue-100 text-blue-800' }
    };

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.pending;
    const Icon = config.icon;

    return (
      <Badge variant={config.variant} className={config.className}>
        <Icon className="h-3 w-3 mr-1" />
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </Badge>
    );
  };

  const handleCreateStaff = async (e: React.FormEvent) => {
    e.preventDefault();
    await onCreateStaff(newStaffData);
    setShowCreateStaffModal(false);
    setNewStaffData({
      email: '',
      password: '',
      firstName: '',
      lastName: '',
      role: 'doctor',
      phone: ''
    });
  };

